mapper.select:
SELECT 
	choose, 
	date_format(receive_date, '%Y-%m-%d %T') as receive_date,
	texture,
	thickness,
	size,
	release_a,
	date_format(release_date, '%Y-%m-%d %T') as release_date
FROM 
	HIOX_TEST

	AND texture = #{texture}
	AND thickness = #{thickness}
	AND size = #{size}
	
		AND date_format(receive_date, '%Y-%m-%d')  >=  #{start_date}
	
	
	 	AND date_format(receive_date, '%Y-%m-%d')  <=  #{end_date}
	 

LIMIT #{pageSize} OFFSET #{offset}
